for f in *.csv
do
grep "MCYST_2" $f > temp_file && mv -f temp_file $f
done

# filter for paired reads only
#line,read_pair,scaffold,mm,insert_dist,mapq,length,reads,start,stop
for f in *.csv
do
awk -F, '$8==2 {print}' $f > filtered_$f
done

# filter for min read ani > 92%
# remove reads with more than 12mm
for f in filtered_*.csv
do
awk -F, '$4<13 {print}' $f > temp_file && mv -f temp_file $f
done

# filter for min/max insert size, min = => 50, max =< 3* mean insert size
for f in filtered_*.csv
do
mean=`awk -F, '{sum += $5} END {if (NR > 0) print sum / NR}' $f`
max=$(echo "$mean * 3" | bc)
awk -v var="$max" -F, '$5<var {print}' $f > temp_file && mv -f temp_file $f
done

for f in filtered_*.csv
do
awk -F, '$5>49 {print}' $f > temp_file && mv -f temp_file $f
done

for f in filtered_*csv
do
  id=${f%.IS_detailed_mapping_info.csv}
  id=${id#filtered_cyano_50-vs-}
  cut -d "," -f2 $f > ${id}_filtered.reads
  awk '{print "@" $1}' ${id}_filtered.reads > temp_file && mv -f temp_file ${id}_filtered.reads
done


for f in [rw]*
do
rm -f $f/$f.1.fastq.gz $f/$f.2.fastq.gz
done
